	// has options?
	if (0 != (p_validbits & ((uint8_t)1U << vb_options))) {
		// 'options': id=2, encoding=varint, tag=0x10
		if (a >= e)
			return -351;
		*a++ = 0x10;
		n = write_varint(a,e-a,m_options);
		if (n <= 0)
			return -352;
		a += n;
	}
