	if (full || has_name()) {
		ascii_string(o,indent,m_name.data(),m_name.size(),"name");
	}
	if (full || (0 != m_tones.size())) {
		gen_indent(o,indent);
		size_t s_tones = m_tones.size();
		o << "tones[" << s_tones << "] = {";
		++indent;
		for (size_t i = 0, e = s_tones; i != e; ++i) {
			gen_indent(o,indent);
			o << '[' << i << "]: ";
			ascii_numeric(o, indent, 0, m_tones[i]);
		}
		--indent;
		gen_indent(o,indent);
		o << '}';
	}
	--indent;
	gen_indent(o,indent);
	o << '}';
