ssize_t Melody::fromMemory(const void *b, ssize_t s)
{
	const uint8_t *a = (const uint8_t *)b;
	const uint8_t *e = a + s;
	while (a < e) {
		varint_t fid;
		int fn = read_varint(a,e-a,&fid);
		if (fn <= 0)
			return -472;
		a += fn;
		switch (fid) {
		case 0xa:	// name id 1, type std::string, coding byte[]
			{
				varint_t v;
				int n = read_varint(a,e-a,&v);
				a += n;
				if ((n <= 0) || ((a+v) > e))
					return -473;
				m_name.assign((const char*)a,v);
				a += v;
			}
			break;
		case 0x14:	// tones id 2, type uint16_t, coding 16bit
			if ((a+1) >= e)
				return -474;
			m_tones.push_back((uint16_t) read_u16(a));
			a += 2;
			break;
		default:
			// unknown field (option unknown=skip)
			{
				ssize_t s = skip_content(a,e-a,fid&7);
				if (s <= 0)
					return -475;
				a += s;
				break;
			}
		}
	}
	assert((a-(const uint8_t *)b) == s);
	if (a > e)
		return -476;
	return a-(const uint8_t *)b;
}

ssize_t Melody::toMemory(uint8_t *b, ssize_t s) const
{
	assert(s >= 0);
	uint8_t *a = b, *e = b + s;
	signed n;
	// has name?
	if (!m_name.empty()) {
		// 'name': id=1, encoding=lenpfx, tag=0xa
		if (a >= e)
			return -477;
		*a++ = 0xa;
		ssize_t name_s = m_name.size();
		n = write_varint(a,e-a,name_s);
		a += n;
		if ((n <= 0) || ((e-a) < name_s))
			return -478;
		memcpy(a,m_name.data(),name_s);
		a += name_s;
	}
	for (auto x : m_tones) {
		// 'tones': id=2, encoding=16bit, tag=0x14
		if (3 > (e-a))
			return -479;
		*a++ = 0x14;
		if ((e-a) < 2)
			return -480;
		write_u16(a,x);
		a += 2;
	}
	assert(a <= e);
	return a-b;
}

void Melody::toWire(void (*put)(uint8_t)) const
{
	if (!m_name.empty()) {
		// 'name': id=1, encoding=lenpfx, tag=0xa
		put(0xa);	// 'name': id=1
		size_t name_s = m_name.size();
		send_varint(put,name_s);
		send_bytes(put,(const uint8_t*) m_name.data(),name_s);
	}
	for (size_t x = 0, y = m_tones.size(); x != y; ++x) {
		// 'tones': id=2, encoding=16bit, tag=0x14
		put(0x14);	// 'tones': id=2
		send_u16(put,m_tones[x]);
	}
}

void Melody::toString(std::string &put) const
{
	if (!m_name.empty()) {
		// 'name': id=1, encoding=lenpfx, tag=0xa
		put.push_back(0xa);	// 'name': id=1
		size_t name_s = m_name.size();
		send_varint(put,name_s);
		put.append((const char *)(const uint8_t*) m_name.data(),name_s);
	}
	for (size_t x = 0, y = m_tones.size(); x != y; ++x) {
		// 'tones': id=2, encoding=16bit, tag=0x14
		put.push_back(0x14);	// 'tones': id=2
		send_u16(put,m_tones[x]);
	}
}

