/*
 * Function for setting an element in dot notation with an ASCII value.
 * It will call the specified parse_ascii function for parsing the value.
 *
 * @return number of bytes successfully parsed or negative value indicating
 *         an error.
 */
int FunctionConfig::setByName(const char *name, const char *value)
{
	if (0 == strcmp(name,"name")) {
		if (value == 0) {
			clear_name();
			return 0;
		}
		m_name = value;
		int r = m_name.size();
		return r;
	}
	if (0 == strcmp(name,"func")) {
		if (value == 0) {
			clear_func();
			return 0;
		}
		m_func = value;
		int r = m_func.size();
		return r;
	}
	if (0 == memcmp(name,"params",6)) {
		if ((name[6] == 0) && (value == 0)) {
			clear_params();
			return 0;
		} else if (name[6] == '[') {
			char *idxe;
			unsigned long x;
			if ((name[7] == '+') && (name[8] == ']')) {
				x = m_params.size();
				m_params.resize(x+1);
				idxe = (char*)(name + 8);
				if (value == 0)
					return 0;
			} else {
				x = strtoul(name+7,&idxe,0);
				if ((idxe[0] != ']') || (idxe == (name+7)))
					return -167;
				if (m_params.size() <= x)
					return -168;
				if ((idxe[1] == 0) && (value == 0)) {
					m_params.erase(m_params.begin()+x);
					return 0;
				}
			}
			if (idxe[1] != 0)
				return -169;
			m_params[x] = value;
			return m_params[x].size();
		}
	}
	return -170;
}

