ssize_t StateMachineConfig::fromMemory(const void *b, ssize_t s)
{
	const uint8_t *a = (const uint8_t *)b;
	const uint8_t *e = a + s;
	while (a < e) {
		varint_t fid;
		union decode_union ud;
		ssize_t x = decode_early(a,e,&ud,&fid);
		if (x < 0)
			return -205;
		a += x;
		switch (fid) {
		case 0xa:	// name id 1, type estring, coding byte[]
			if ((ssize_t)ud.vi > e-a) {
				return -206;
			}
			m_name.assign((const char*)a,ud.vi);
			a += ud.vi;
			break;
		case 0x10:	// ini_st id 2, type uint8_t, coding varint
			set_ini_st((uint8_t)ud.u32);
			break;
		case 0x1a:	// states id 3, type StateConfig, coding byte[]
			m_states.emplace_back();
			if (((ssize_t)ud.vi > 0) && ((ssize_t)ud.vi <= (e-a))) {
				int n;
				n = m_states.back().fromMemory((const uint8_t*)a,ud.vi);
				if (n != (ssize_t)ud.vi)
					return -207;
				a += ud.vi;
			}
			break;
		case 0x23:	// persistent id 4, type bool, coding 8bit
			set_persistent(ud.u8);
			break;
		default:
			if ((fid & 7) == 2) {
				// need only to skip len prefixed data
				a += ud.vi;
				if (a > e)
					return -208;
			}
		}
	}
	assert((a-(const uint8_t *)b) == s);
	if (a > e)
		return -209;
	return a-(const uint8_t *)b;
}

ssize_t StateMachineConfig::toMemory(uint8_t *b, ssize_t s) const
{
	assert(s >= 0);
	uint8_t *a = b, *e = b + s;
	signed n;
	// has name?
	if (!m_name.empty()) {
		// 'name': id=1, encoding=lenpfx, tag=0xa
		if (a >= e)
			return -210;
		*a++ = 0xa;
		n = encode_bytes(m_name,a,e);
		if (n < 0)
			return -211;
		a += n;
	}
	// has ini_st?
	if (0 != (p_validbits & ((uint8_t)1U << 0))) {
		// 'ini_st': id=2, encoding=varint, tag=0x10
		if (a >= e)
			return -212;
		*a++ = 0x10;
		n = write_varint(a,e-a,m_ini_st);
		if (n <= 0)
			return -213;
		a += n;
	}
	for (const auto &x : m_states) {
		// 'states': id=3, encoding=lenpfx, tag=0x1a
		if (a >= e)
			return -214;
		*a++ = 0x1a;
		ssize_t states_ws = x.calcSize();
		n = write_varint(a,e-a,states_ws);
		a += n;
		if ((n <= 0) || (states_ws > (e-a)))
			return -215;
		n = x.toMemory(a,e-a);
		a += n;
		assert(n == states_ws);
	}
	// has persistent?
	if (m_persistent != false) {
		// 'persistent': id=4, encoding=8bit, tag=0x23
		if (2 > (e-a))
			return -216;
		*a++ = 0x23;
		*a++ = m_persistent;
	}
	assert(a <= e);
	return a-b;
}

void StateMachineConfig::toJSON(stream &json, unsigned indLvl) const
{
	char fsep = '{';
	++indLvl;
	if (has_name()) {
		fsep = json_indent(json,indLvl,fsep,"name");
		json_cstr(json,m_name.c_str());
	}
	if (has_ini_st()) {
		fsep = json_indent(json,indLvl,fsep,"ini_st");
		to_decstr(json,(unsigned) m_ini_st);
	}
	if (size_t s = m_states.size()) {
		fsep = json_indent(json,indLvl,fsep);
		indLvl += 2;
		json << "\"states\":[\n";
		size_t i = 0;
		for (;;) {
			json_indent(json,indLvl,0);
			m_states[i].toJSON(json,indLvl);
			++i;
			if (i == s)
				break;
			json << ",\n";
		}
		indLvl -= 2;
		json.put('\n');
		json_indent(json,indLvl,0);
		json.put(']');
	}
	if (has_persistent()) {
		fsep = json_indent(json,indLvl,fsep,"persistent");
		json << (m_persistent ? "true" : "false");
	}
	if (fsep == '{')
		json.put('{');
	json.put('\n');
	--indLvl;
	json_indent(json,indLvl,0);
	json.put('}');
	if (indLvl == 0)
		json.put('\n');
}

size_t StateMachineConfig::calcSize() const
{
	size_t r = 0;	// required size, default is fixed length
	// optional string name, id 1
	if (has_name()) {
		size_t name_s = m_name.size();
		r += name_s + wiresize(name_s) + 1 /* tag(name) 0x8 */;
	}
	// optional uint8 ini_st, id 2
	if (has_ini_st()) {
		r += wiresize((varint_t)m_ini_st) + 1 /* tag(ini_st) 0x10 */;
	}
	// repeated StateConfig states, id 3
	// repeated message states
	for (size_t x = 0, y = m_states.size(); x < y; ++x) {
		size_t s = m_states[x].calcSize();
		r += wiresize(s);
		r += s + 1 /* tag(states) 0x18 */;
	}
	// optional bool persistent, id 4
	if (has_persistent()) {
		r += 2;
	}
	return r;
}

bool StateMachineConfig::operator == (const StateMachineConfig &r) const
{
	if (p_validbits != r.p_validbits)
		return false;
	if (has_name() && (!(m_name == r.m_name)))
		return false;
	if (has_ini_st() && (!(m_ini_st == r.m_ini_st)))
		return false;
	if (!(m_states == r.m_states))
		return false;
	if (has_persistent() && (!(m_persistent == r.m_persistent)))
		return false;
	return true;
}


/*
 * Function for setting an element in dot notation with an ASCII value.
 * It will call the specified parse_ascii function for parsing the value.
 *
 * @return number of bytes successfully parsed or negative value indicating
 *         an error.
 */
int StateMachineConfig::setByName(const char *name, const char *value)
{
	if (0 == strcmp(name,"name")) {
		if (value == 0) {
			clear_name();
			return 0;
		}
		m_name = value;
		int r = m_name.size();
		return r;
	}
	if (0 == strcmp(name,"ini_st")) {
		if (value == 0) {
			clear_ini_st();
			return 0;
		}
		int r = parse_ascii_u8(&m_ini_st,value);
		if (r > 0)
			p_validbits |= ((uint8_t)1U << 0);
		return r;
	}
	if (0 == memcmp(name,"states",6)) {
		if ((name[6] == 0) && (value == 0)) {
			clear_states();
			return 0;
		} else if (name[6] == '[') {
			char *idxe;
			unsigned long x;
			if ((name[7] == '+') && (name[8] == ']')) {
				x = m_states.size();
				m_states.resize(x+1);
				idxe = (char*)(name + 8);
				if (value == 0)
					return 0;
			} else {
				x = strtoul(name+7,&idxe,0);
				if ((idxe[0] != ']') || (idxe == (name+7)))
					return -217;
				if (m_states.size() <= x)
					return -218;
				if ((idxe[1] == 0) && (value == 0)) {
					m_states.erase(m_states.begin()+x);
					return 0;
				}
			}
			if (idxe[1] != '.')
				return -219;
			return m_states[x].setByName(idxe+2,value);
		}
	}
	if (0 == strcmp(name,"persistent")) {
		if (value == 0) {
			clear_persistent();
			return 0;
		}
		int r = parse_ascii_bool(&m_persistent,value);
		return r;
	}
	return -220;
}

